import axios from 'axios'
import React, { memo, useEffect, useState } from 'react'

var error = "";
const Details = ({ isbn }) => {
  const [detail, setDetail] = useState([]);
  useEffect(() => {
    axios.get(`https://openlibrary.org/api/books?bibkeys=ISBN:${isbn}&jscmd=details&format=json`)
      .then(resp => {
        for (let x in resp.data)
          setDetail(resp.data[x])
      })
      .catch(err => { error = err })
  }, [isbn])
  return (
    <div id="detailsContainer">
      {detail.length === 0  ? "Loading" :
      <>
      <div className='bookImage'>
          <img src={(detail.details.covers === "" )? `https://covers.openlibrary.org/b/id/${detail.details.covers}-L.jpg` : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfYEG2AZWFMEjpExDUQUtJXaX175rgJi-5Ji-kSVO_wTHlBNr1AGAtaGz4P7j0qarq_Gg&usqp=CAU' } alt={detail.details.full_title} style={{ width:'10vh'}}/>

          {console.log(detail)}
      </div>
      <div className="aboutBook">

      </div>
      </>
      }

    </div>
  )
}

export default memo(Details)
