import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import SearchCard from './SearchCard';
import './Styles/SearchPage.css'

const SearchPage = ({ text, setText }) => {

  var [result, setResult] = useState([]);

  useEffect(() => {
    axios.get(`https://openlibrary.org/search.json?q=${text}&mode=ebooks&has_fulltext=true`)
      .then(resp => {
        console.log(resp.data.docs);
        setResult(resp.data)
      })
      .catch((e) => {
        console.log(e.code);
      })
  }, [text])


  return (
    <div id="searchContainer">
      <div id="searchPageContainer">
        <Link><h1>Digital Library</h1></Link>
        <input type="text" value={text} onChange={(e) => setText(e.target.value)} />
      </div>

      {result.length === 0 ? " " :
        <div id="searchListContainer">
          <h1>Search Result</h1>
          <div id='filters'>
            <span>{result.numFound} results</span><span>Relevance</span><span>Most Editions</span><span>First Published</span><span>Most Recent</span><span>Random</span>
          </div>
          <div id="searchCardContainer">
            {result.docs.map((val, index) => {
              return (
                <SearchCard val={val} key={index} />
              )
            })
            }
          </div>
        </div>
      }

    </div>
  )
}

export default SearchPage
