import axios from 'axios';
import React, { useEffect, useState } from 'react'

const SearchPage = ({ text, setText }) => {

  var [result, setResult] = useState([]);
  useEffect(() => {
    axios.get(`https://openlibrary.org/search.json?q=${text}&mode=ebooks&has_fulltext=true`)
      .then(resp => {
        setResult(resp.data)
      })
      .catch((e) => {
        console.log(e.code);
      })
  }, [text])

  return (
    <div id="searchContainer">
      <div id="searchPageContainer">
        <h1>Digital Library</h1>
        <input type="text" value={text} onChange={(e) => setText(e.target.value)} />
      </div>
      <div id="searchListContainer">
        <h1>Search</h1>
        <div id='filters'>
          <span>Relevance</span><span>Most Editions</span><span>First Published</span><span>Most Recent</span><span>Random</span>
        </div>

      {result}

      </div>
    </div>
  )
}

export default SearchPage
