import axios from 'axios'
import React, { memo, useEffect, useState } from 'react'

var error = "";
const Details = ({ isbn }) => {
  const [detail, setDetail] = useState([]);
  useEffect(() => {
    axios.get(`https://openlibrary.org/api/books?bibkeys=ISBN:${isbn}&jscmd=details&format=json`)
      .then(resp => {
        for (let x in resp.data)
          setDetail(resp.data[x])

          console.log(detail);
      })
      .catch(err => { error = err })
  }, [isbn])
  return (
    <div id="detailsContainer">
      {detail.length === 0  ? "" :
      <>
      <div className='bookImage'>
          <img src={detail.thumbnail_url !== "" ? 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/1024px-No_image_available.svg.png': detail.thumbnail_url} alt={detail.details.full_title}/>
      </div>
      <div className="aboutBook">

      </div>
      </>
      }

    </div>
  )
}

export default memo(Details)
